pragma solidity >=0.5.0 <0.9.0;

contract Company
{
    string public symbol;
    address public owner;
    
    // Total stock
    uint issuedShares;
    // Publicly available stock
    uint floatShares;
    
    address[] buyOrders;
    address[] sellOrders;
    
    constructor(address _owner, string memory _symbol, uint _issued, uint _float) public
    {
        require(_float < _issued, "Total shares is not greater than float.");
        
        owner = _owner;
        symbol = _symbol;
        issuedShares = _issued;
        floatShares = _float;
    }
    
    function isOwner(address _addr) public view returns (bool)
    {
        return _addr == owner;
    }
    
    function changeOwnership(address _oldOwner, address _newOwner) public returns(address)
	{
		require(_oldOwner == owner, "Ownership change request made from non-authoratative account");
		
		owner = _newOwner;
		
		return owner;
	}
    
}
